pub enum Buffer {
    Unbound {
        size: buffer::Offset,
        usage: buffer::Usage,
    },
    Bound {
        buffer: RawBuffer,
        range: Range<buffer::Offset>,
    },
}

pub enum Fence {
    Idle {
        signaled: bool,
    },
    Pending(<GlContext as glow::HasContext>::Fence),
}

pub enum ImageView {
    Renderbuffer {
        raw: Renderbuffer,
        aspects: format::Aspects,
    },
    Texture {
        target: TextureTarget,
        raw: Texture,
        is_3d: bool,
        sub: i::SubresourceRange,
    },
}

pub enum ShaderModule {
    Raw(Shader),
    Spirv(Vec<u32>),
    Naga(naga::Module, Vec<u32>),
}
