pub enum AttachmentClear {
    Color {
        index: usize,
        value: ClearColor,
    },
    DepthStencil {
        depth: Option<pso::DepthValue>,
        stencil: Option<pso::StencilValue>,
    },
}

pub enum MapError {
    OutOfMemory(OutOfMemory),
    OutOfBounds,
    MappingFailed,
    Access,
}

pub enum ShaderError {
    Unsupported,
    CompilationFailed(String),
    OutOfMemory(OutOfMemory),
}

pub enum Kind {
    D1(Size, Layer),
    D2(Size, Size, Layer, NumSamples),
    D3(Size, Size, Size),
}

pub enum Barrier<'a, B: Backend> {
    AllBuffers(Range<buffer::Access>),
    AllImages(Range<image::Access>),
    Buffer {
        states: Range<buffer::State>,
        target: &'a B::Buffer,
        range: buffer::SubRange,
        families: Option<Range<queue::QueueFamilyId>>,
    },
    Image {
        states: Range<image::State>,
        target: &'a B::Image,
        range: image::SubresourceRange,
        families: Option<Range<queue::QueueFamilyId>>,
    },
}

pub enum BufferDescriptorType {
    Storage {
        read_only: bool,
    },
    Uniform,
}

pub enum BufferDescriptorFormat {
    Structured {
        dynamic_offset: bool,
    },
    Texel,
}

pub enum ImageDescriptorType {
    Sampled {
        with_sampler: bool,
    },
    Storage {
        read_only: bool,
    },
}

pub enum DescriptorType {
    Sampler,
    Image {
        ty: ImageDescriptorType,
    },
    Buffer {
        ty: BufferDescriptorType,
        format: BufferDescriptorFormat,
    },
    InputAttachment,
}

pub enum AllocationError {
    OutOfMemory(OutOfMemory),
    OutOfPoolMemory,
    FragmentedPool,
    IncompatibleLayout,
}

pub enum PrimitiveAssemblerDesc<'a, B: Backend> {
    Vertex {
        buffers: &'a [VertexBufferDesc],
        attributes: &'a [AttributeDesc],
        input_assembler: InputAssemblerDesc,
        vertex: EntryPoint,
        tessellation: Option<(EntryPoint, EntryPoint)>,
        geometry: Option<EntryPoint>,
    },
    Mesh {
        task: Option<EntryPoint>,
        mesh: EntryPoint,
    },
}

pub enum BlendOp {
    Add {
        src: Factor,
        dst: Factor,
    },
    Sub {
        src: Factor,
        dst: Factor,
    },
    RevSub {
        src: Factor,
        dst: Factor,
    },
    Min,
    Max,
}

pub enum SwapchainError {
    OutOfMemory(device::OutOfMemory),
    DeviceLost(device::DeviceLost),
    SurfaceLost(SurfaceLost),
    WindowInUse,
}

pub enum AcquireError {
    OutOfMemory(device::OutOfMemory),
    NotReady {
        timeout: bool,
    },
    OutOfDate(OutOfDate),
    SurfaceLost(SurfaceLost),
    DeviceLost(device::DeviceLost),
}

pub enum PresentError {
    OutOfMemory(device::OutOfMemory),
    OutOfDate(OutOfDate),
    SurfaceLost(SurfaceLost),
    DeviceLost(device::DeviceLost),
}
