pub enum SetupError {
    NoDefaultOutputDevice,
    DefaultStreamConfigError(DefaultStreamConfigError),
    BuildStreamError(BuildStreamError),
    PlayStreamError(PlayStreamError),
}

pub enum AddSoundError {
    SoundLimitReached,
    NoTrackWithIndex(TrackIndex),
    NoGroupWithId(GroupId),
    CommandProducerError(CommandError),
}

pub enum AddArrangementError {
    ArrangementLimitReached,
    NoTrackWithIndex(TrackIndex),
    NoGroupWithId(GroupId),
    CommandProducerError(CommandError),
}

pub enum AddGroupError {
    GroupLimitReached,
    NoGroupWithId(GroupId),
    CommandProducerError(CommandError),
}

pub enum AddSubTrackError {
    TrackLimitReached,
    NonexistentParentTrack(SubTrackId),
    NonexistentSendTrack(SendTrackId),
    CommandProducerError(CommandError),
}

pub enum AddStreamError {
    StreamLimitReached,
    NoTrackWithIndex(TrackIndex),
    CommandProducerError(CommandError),
}

pub enum StartSequenceError {
    SequenceError(SequenceError),
    NoGroupWithId(GroupId),
    CommandProducerError(CommandError),
}

pub enum AddEffectError {
    EffectLimitReached,
    NoEffectWithId(EffectId),
    CommandProducerError(CommandError),
}

pub enum Value<T: From<f64> + Into<f64> + Copy> {
    Fixed(T),
    Parameter(ParameterId, Mapping),
    Random(T, T),
}
